package cms

import (
	"testing"

	"github.com/gmrtd/gmrtd/utils"
)

func TestSignedDataCertPoolErrors(t *testing.T) {
	// invalid signed-data
	{
		sdBytes := utils.HexToBytes("030A")
		certBytes := utils.HexToBytes("")

		_, err := CreateCertPoolFromSignedData(sdBytes, certBytes)
		if err == nil {
			t.Fatalf("error expected")
		}
	}

	// invalid cert
	{
		// NB using SOD from SG passport, not perfectly valid (econtent), but still valid signed-data
		sdBytes := utils.HexToBytes("")
		certBytes := utils.HexToBytes("030A")

		_, err := CreateCertPoolFromSignedData(sdBytes, certBytes)
		if err == nil {
			t.Fatalf("error expected")
		}
	}

	// valid signed-data & cert BUT cert won't verify this signed-data
	{
		// NB using SOD from SG passport, not perfectly valid (econtent), but still valid signed-data
		sdBytes := utils.HexToBytes("")
		certBytes := utils.HexToBytes("")

		_, err := CreateCertPoolFromSignedData(sdBytes, certBytes)
		if err == nil {
			t.Fatalf("error expected")
		}
	}
}
