package gmrtd

import (
	"testing"
)

func TestNewDG2NoData(t *testing.T) {
	if NewDG2(nil) != nil {
		t.Errorf("Should be nil when no input data provided")
	}
	if NewDG2([]byte{}) != nil {
		t.Errorf("Should be nil when no input data provided")
	}
}

func TestNewDG2(t *testing.T) {
	data := HexToBytes(
		"")

	dg2 := NewDG2(data)

	// TODO - basic check should expand
	if len(dg2.BITs[0].BDB.Facial.Images[0].Data) != 5099 {
		t.Errorf("Invalid data")
	}
}
