func TestNewDG2Iso19794(t *testing.T) {
	data := utils.HexToBytes(
		"")

	expImageBytes := utils.HexToBytes("")

	var doc Document

	err := doc.NewDG(2, data)

	if err != nil {
		t.Errorf("Unexpected error: %s", err)
	}

	if !bytes.Equal(expImageBytes, doc.Mf.Lds1.Dg2.BITs[0].BDB.Iso19794.Facial.Images[0].Data) {
		t.Errorf("Image bytes differ to expected")
	}
}

//go:embed iso39794/test_data/ICAO_39794_5_AP_DG2_AllFields.dat
var dg2AllFields39794 []byte

//go:embed iso39794/test_data/ICAO_39794_5_AP_DG2_Face.jpg
var dg2AllFields39794Face []byte

func TestNewDG2AllFields39794(t *testing.T) {
	var doc Document

	err := doc.NewDG(2, dg2AllFields39794)
	if err != nil {
		t.Fatalf("Unexpected error: %s", err)
	}

	// verify that the photo matches the reference data
	if !bytes.Equal(doc.Mf.Lds1.Dg2.Images[0], dg2AllFields39794Face) {
		t.Fatalf("DG2 photo differs to expected")
	}
}

