package passiveauth

import (
	"testing"

	"github.com/gmrtd/gmrtd/document"
	"github.com/gmrtd/gmrtd/utils"
)

/*
* Note: these tests largely mirror those for SOD / CardSecurity
 */

func TestPassiveAuth(t *testing.T) {
	testCases := []struct {
		cardSecurityFileBytes []byte
		sodFileBytes          []byte
	}{
		// NB CardSecurity file not present for most
		{
			// AT
			cardSecurityFileBytes: nil,
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// DE
			cardSecurityFileBytes: utils.HexToBytes(""),
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// FI
			cardSecurityFileBytes: utils.HexToBytes(""),
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// FR
			cardSecurityFileBytes: nil,
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// MY
			// RSA-PSS
			cardSecurityFileBytes: nil,
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// NZ
			cardSecurityFileBytes: nil,
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// PH
			cardSecurityFileBytes: nil,
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// RU
			cardSecurityFileBytes: nil,
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// SG
			cardSecurityFileBytes: nil,
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// UK
			cardSecurityFileBytes: nil,
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// US
			cardSecurityFileBytes: nil,
			sodFileBytes:          utils.HexToBytes(""),
		},
	}
	for _, tc := range testCases {
		var err error
		var doc document.Document

		if tc.cardSecurityFileBytes != nil {
			doc.Mf.CardSecurity, err = document.NewCardSecurity(tc.cardSecurityFileBytes)
			if err != nil {
				t.Errorf("Unexpected error: %s", err)
			}
		}

		{
			doc.Mf.Lds1.Sod, err = document.NewSOD(tc.sodFileBytes)
			if err != nil {
				t.Errorf("Unexpected error: %s", err)
			}
		}

		// perform passive authentication
		// - throws error if verification fails
		err = PassiveAuth(&doc)
		if err != nil {
			t.Errorf("Unexpected error: %s", err)
		}
	}
}

func TestPassiveAuthErrors(t *testing.T) {
	// NB we only expect errors during the final check (PassiveAuth)
	testCases := []struct {
		cardSecurityFileBytes []byte
		sodFileBytes          []byte
	}{
		{
			// adapted from DE
			// valid data, but with CardSecurity file corrupted (last byte: 99->98)
			cardSecurityFileBytes: utils.HexToBytes(""),
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// adapted from DE
			// valid data, but with SOD file corrupted (last byte: C7->C8)
			cardSecurityFileBytes: utils.HexToBytes(""),
			sodFileBytes:          utils.HexToBytes(""),
		},
	}
	for _, tc := range testCases {
		var err error
		var doc document.Document

		if tc.cardSecurityFileBytes != nil {
			doc.Mf.CardSecurity, err = document.NewCardSecurity(tc.cardSecurityFileBytes)
			if err != nil {
				t.Errorf("Unexpected error: %s", err)
			}
		}

		{
			doc.Mf.Lds1.Sod, err = document.NewSOD(tc.sodFileBytes)
			if err != nil {
				t.Errorf("Unexpected error: %s", err)
			}
		}

		// perform passive authentication
		err = PassiveAuth(&doc)
		if err == nil {
			t.Errorf("Expected error")
		}
	}
}
