package passiveauth

import (
	"testing"

	"github.com/gmrtd/gmrtd/cms"
	"github.com/gmrtd/gmrtd/document"
	"github.com/gmrtd/gmrtd/tlv"
	"github.com/gmrtd/gmrtd/utils"
)

/*
* Note: these tests largely mirror those for SOD / CardSecurity
 */

func TestPassiveAuth(t *testing.T) {
	/*
	* Note: we use a dummy MRZ that does NOT contain any PII, to enable Passive-Authentication
	*       to determine the Issuer Country-Code
	*
	*       e.g. "P<D<<DOE<<JOHN<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<D123456785UTO6508092M3505207<<<<<<<<<<<<<<<0"
	*               *** (issuer country-code)
	 */

	/*
	 * get the (default) CSCA Certificate-Pool
	 */
	cscaCertPool, err := cms.GetDefaultMasterList()
	if err != nil {
		t.Fatalf("CscaCertPool error: %s", err)
	}

	testCases := []struct {
		dg1Mrz                string
		cardSecurityFileBytes []byte
		sodFileBytes          []byte
	}{
		// NB CardSecurity file not present for most
		{
			// AT
			dg1Mrz:                "P<AUTDOE<<JOHN<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<D123456785UTO6508092M3505207<<<<<<<<<<<<<<<0",
			cardSecurityFileBytes: nil,
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// DE
			dg1Mrz:                "P<D<<DOE<<JOHN<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<D123456785UTO6508092M3505207<<<<<<<<<<<<<<<0",
			cardSecurityFileBytes: utils.HexToBytes(""),
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// FI
			dg1Mrz:                "P<FINDOE<<JOHN<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<D123456785UTO6508092M3505207<<<<<<<<<<<<<<<0",
			cardSecurityFileBytes: utils.HexToBytes(""),
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// FR
			dg1Mrz:                "P<FRADOE<<JOHN<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<D123456785UTO6508092M3505207<<<<<<<<<<<<<<<0",
			cardSecurityFileBytes: nil,
			sodFileBytes:          utils.HexToBytes(""),
		},
		// Issue #14: [FEAT] CSCA for Indonesia is missing from Master List
		/*
			{
				// ID
				dg1Mrz:                "P<IDNDOE<<JOHN<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<D123456785UTO6508092M3505207<<<<<<<<<<<<<<<0",
				cardSecurityFileBytes: nil,
				sodFileBytes:          utils.HexToBytes(""),
			},
		*/
		{
			// MY
			// RSA-PSS
			dg1Mrz:                "P<MYSDOE<<JOHN<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<D123456785UTO6508092M3505207<<<<<<<<<<<<<<<0",
			cardSecurityFileBytes: nil,
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// NZ
			dg1Mrz:                "P<NZLDOE<<JOHN<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<D123456785UTO6508092M3505207<<<<<<<<<<<<<<<0",
			cardSecurityFileBytes: nil,
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// PH
			dg1Mrz:                "P<PHLDOE<<JOHN<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<D123456785UTO6508092M3505207<<<<<<<<<<<<<<<0",
			cardSecurityFileBytes: nil,
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// RU
			dg1Mrz:                "P<RUSDOE<<JOHN<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<D123456785UTO6508092M3505207<<<<<<<<<<<<<<<0",
			cardSecurityFileBytes: nil,
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// SG
			dg1Mrz:                "P<SGPDOE<<JOHN<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<D123456785UTO6508092M3505207<<<<<<<<<<<<<<<0",
			cardSecurityFileBytes: nil,
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// UK
			dg1Mrz:                "P<GBRDOE<<JOHN<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<D123456785UTO6508092M3505207<<<<<<<<<<<<<<<0",
			cardSecurityFileBytes: nil,
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// US
			dg1Mrz:                "P<USADOE<<JOHN<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<D123456785UTO6508092M3505207<<<<<<<<<<<<<<<0",
			cardSecurityFileBytes: nil,
			sodFileBytes:          utils.HexToBytes(""),
		},
	}
	for _, tc := range testCases {
		var err error
		var doc document.Document

		// build DG1 from MRZ
		{
			dg1Bytes := tlv.NewTlvConstructedNode(0x61).AddChild(tlv.NewTlvSimpleNode(0x5f1f, []byte(tc.dg1Mrz))).Encode()

			doc.Mf.Lds1.Dg1, err = document.NewDG1(dg1Bytes)
			if err != nil {
				t.Errorf("Unexpected error: %s", err)
			}
		}

		if tc.cardSecurityFileBytes != nil {
			doc.Mf.CardSecurity, err = document.NewCardSecurity(tc.cardSecurityFileBytes)
			if err != nil {
				t.Errorf("Unexpected error: %s", err)
			}
		}

		{
			doc.Mf.Lds1.Sod, err = document.NewSOD(tc.sodFileBytes)
			if err != nil {
				t.Errorf("Unexpected error: %s", err)
			}
		}

		// perform passive authentication
		// - throws error if verification fails
		err = PassiveAuth(&doc, cscaCertPool)
		if err != nil {
			t.Errorf("Unexpected error: %s", err)
		}
	}
}

func TestPassiveAuthErrors(t *testing.T) {
	/*
	* Note: we use a dummy MRZ that does NOT contain any PII, to enable Passive-Authentication
	*       to determine the Issuer Country-Code
	*
	*       e.g. "P<D<<DOE<<JOHN<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<D123456785UTO6508092M3505207<<<<<<<<<<<<<<<0"
	*               *** (issuer country-code)
	 */

	/*
	 * get the (default) CSCA Certificate-Pool
	 */
	cscaCertPool, err := cms.GetDefaultMasterList()
	if err != nil {
		t.Fatalf("CscaCertPool error: %s", err)
	}

	// NB we only expect errors during the final check (PassiveAuth)
	testCases := []struct {
		dg1Mrz                string
		cardSecurityFileBytes []byte
		sodFileBytes          []byte
	}{
		{
			// adapted from DE
			// valid data, but with CardSecurity file corrupted (last byte: 99->98)
			dg1Mrz:                "P<D<<DOE<<JOHN<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<D123456785UTO6508092M3505207<<<<<<<<<<<<<<<0",
			cardSecurityFileBytes: utils.HexToBytes(""),
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// adapted from DE
			// valid data, but with SOD file corrupted (last byte: C7->C8)
			dg1Mrz:                "P<D<<DOE<<JOHN<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<D123456785UTO6508092M3505207<<<<<<<<<<<<<<<0",
			cardSecurityFileBytes: utils.HexToBytes(""),
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// adapted from DE
			// valid data, but with MRZ country changed to a fake country (XYZ) to trigger country-code conversion (3->2 letter) error
			dg1Mrz:                "P<XYZDOE<<JOHN<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<D123456785UTO6508092M3505207<<<<<<<<<<<<<<<0",
			cardSecurityFileBytes: utils.HexToBytes(""),
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// adapted from DE
			// valid data, but with MRZ country changed to Palestinian Territory (PSE) which is a valid country but does NOT (currently) have CSCA certificates
			dg1Mrz:                "P<PSEDOE<<JOHN<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<D123456785UTO6508092M3505207<<<<<<<<<<<<<<<0",
			cardSecurityFileBytes: utils.HexToBytes(""),
			sodFileBytes:          utils.HexToBytes(""),
		},
		{
			// adapted from DE
			// valid data, but with missing SOD
			dg1Mrz:                "P<D<<DOE<<JOHN<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<D123456785UTO6508092M3505207<<<<<<<<<<<<<<<0",
			cardSecurityFileBytes: utils.HexToBytes(""),
			sodFileBytes:          nil,
		},
		{
			// adapted from SG
			// but with MRZ country set to GBR(UK) to trigger CSCA country cert failure
			dg1Mrz:                "P<GBRDOE<<JOHN<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<D123456785UTO6508092M3505207<<<<<<<<<<<<<<<0",
			cardSecurityFileBytes: nil,
			sodFileBytes:          utils.HexToBytes(""),
		},
	}
	for _, tc := range testCases {
		var err error
		var doc document.Document

		// build DG1 from MRZ
		{
			dg1Bytes := tlv.NewTlvConstructedNode(0x61).AddChild(tlv.NewTlvSimpleNode(0x5f1f, []byte(tc.dg1Mrz))).Encode()

			doc.Mf.Lds1.Dg1, err = document.NewDG1(dg1Bytes)
			if err != nil {
				t.Errorf("Unexpected error: %s", err)
			}
		}

		if tc.cardSecurityFileBytes != nil {
			doc.Mf.CardSecurity, err = document.NewCardSecurity(tc.cardSecurityFileBytes)
			if err != nil {
				t.Errorf("Unexpected error: %s", err)
			}
		}

		if tc.sodFileBytes != nil {
			doc.Mf.Lds1.Sod, err = document.NewSOD(tc.sodFileBytes)
			if err != nil {
				t.Errorf("Unexpected error: %s", err)
			}
		}

		// perform passive authentication
		err = PassiveAuth(&doc, cscaCertPool)
		if err == nil {
			t.Errorf("Expected error")
		}
	}
}
